import React, { useEffect, useState, useRef } from "react";
import { useDispatch, useSelector } from 'react-redux';
import { makeStyles, useTheme } from "@material-ui/core/styles";
import Input from "@material-ui/core/Input";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import ListItemText from "@material-ui/core/ListItemText";
import Select from "@material-ui/core/Select";
import Checkbox from "@material-ui/core/Checkbox";
import Grid from "@material-ui/core/Grid";
import MainOrderContainer from "./old_main_order_container";
import Button from "@material-ui/core/Button";
import MainOrderTable from './main_order.table';

import { fetchMainOrderStart } from '../../redux/main-order/main_order.actions';
import { selectSites, selectMainOrderList, selectVendor, selectMonth } from '../../redux/main-order/main_order.selector';

const useStyles = makeStyles((theme) => ({
    formControl: {
      margin: theme.spacing(1),
      minWidth: 120,
      maxWidth: 300,
    },
    chips: {
      display: "flex",
      flexWrap: "wrap",
    },
    chip: {
      margin: 2,
    },
    noLabel: {
      marginTop: theme.spacing(3),
    },
    tableContainer: {
      paddingBottom: "2em",
    },
  }));
  
  const ITEM_HEIGHT = 35;
  const ITEM_PADDING_TOP = 1;
  const MenuProps = {
    PaperProps: {
      style: {
        maxHeight: ITEM_HEIGHT * 6.5 + ITEM_PADDING_TOP,
        width: 190,
      },
    },
  };

export default function MainOrderPage() {
  const componentRef = useRef();
  const dispatch = useDispatch();
  const classes = useStyles();
  const theme = useTheme();
  const [siteName, setSiteName] = React.useState([]);
  const [VendorName, setVendorName] = React.useState([]);
  const [month, setMonth] = React.useState([]);
  const [filteredData, setFilteredData] = React.useState([]);

  /* Selected Iputs */
  const [selectedSite, setSelectedSite] = React.useState([]);
  const [selectedVendor, setSelectedVendor] = React.useState([]);
  const [selectedMonth, setSelectedMonth] = React.useState([]);


  const handleChangeSite = (event) => {
    setSelectedSite(event.target.value);
  };

  const handleChangeVendor = (event) => {
    setSelectedVendor(event.target.value);
  };

  const handleChangeMonth = (event) => {
    setSelectedMonth(event.target.value);
  };

  //Selectors
  const mainOrderList = useSelector((state) => selectMainOrderList(state));
  const sitesList = useSelector((state) => selectSites(state));
  const vendorList = useSelector((state) => selectVendor(state));
  const monthList = useSelector((state) => selectMonth(state));

  useEffect(() => {
    dispatch(
			fetchMainOrderStart()
		);

        const result = mainOrderList.data.filter((item) => {
            const { site_name, vendor_name, month } = item;
  
            return (
              selectedSite.includes(site_name) &&
              selectedVendor.includes(vendor_name) &&
              selectedMonth.includes(month)
            );
          });
          console.log(result);
          selectedSite.length ? setFilteredData(result) : setFilteredData([]);
  }, []);

  console.log('Selected', selectedSite, selectedVendor, selectedMonth, mainOrderList);

  return (
    <React.Fragment>
        <Grid
        container
        justify='center'
        direction='row'
        className={classes.tableContainer}
        spacing={10}
      >
        <Grid item>
          <FormControl className={classes.formControl}>
            <InputLabel id='demo-mutiple-checkbox-label'>Site Name</InputLabel>
            <Select
              labelId='demo-mutiple-checkbox-label'
              id='demo-mutiple-checkbox'
              multiple
              value={selectedSite}
              onChange={handleChangeSite}
              input={<Input />}
              renderValue={(selected) => selected.join(", ")}
              MenuProps={MenuProps}
            >
              {sitesList.map((name1) => (
                <MenuItem key={name1} value={name1}>
                  <Checkbox checked={selectedSite.indexOf(name1) > -1} />
                  <ListItemText primary={name1} />
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </Grid>
        <Grid item>
          <FormControl className={classes.formControl}>
            <InputLabel id='demo-mutiple-checkbox-label'>
              Vendor Name
            </InputLabel>
            <Select
              labelId='demo-mutiple-checkbox-label'
              id='demo-mutiple-checkbox'
              multiple
              value={selectedVendor}
              onChange={handleChangeVendor}
              input={<Input />}
              renderValue={(selected) => selected.join(", ")}
              MenuProps={MenuProps}
            >
              {vendorList.map((name) => (
                <MenuItem key={name} value={name}>
                  <Checkbox checked={selectedVendor.indexOf(name) > -1} />
                  <ListItemText primary={name} />
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </Grid>
        <Grid item>
          <FormControl className={classes.formControl}>
            <InputLabel id='demo-mutiple-checkbox-label'>Month</InputLabel>
            <Select
              labelId='demo-mutiple-checkbox-label'
              id='demo-mutiple-checkbox'
              multiple
              value={selectedMonth}
              onChange={handleChangeMonth}
              input={<Input />}
              renderValue={(selected) => selected.join(", ")}
              MenuProps={MenuProps}
            >
              {monthList.map((name) => (
                <MenuItem key={name} value={name}>
                  <Checkbox checked={selectedMonth.indexOf(name) > -1} />
                  <ListItemText primary={name} />
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </Grid>
      </Grid>
      <MainOrderTable selectedSite={selectedSite} selectedVendor={selectedVendor} selectedMonth={selectedMonth} />
      
    </React.Fragment>
  );
}
